import React, { useEffect, useState } from 'react';
import './Sidebar.css';
import { Avatar, IconButton } from '@material-ui/core';
import SearchIcon from '@material-ui/icons/Search';
import MessageIcon from '@material-ui/icons/Message';
import SidebarChat from './SidebarChat';
import { useSelector } from 'react-redux';
import { selectUser } from './features/userSlice';
import db, {auth} from './Firebase'
import axios from './axios.js'

const pusher = new Pusher({
    appId: "1191956",
    key: "4d6e0c3fffd1de8e1636",
    secret: "e7f57f900c90d7db8da8",
    cluster: "eu",
    useTLS: true
  });

function Sidebar() {

    const user = useSelector(selectUser);

    const [chats, setChats] = useState([]);

    const getChats = () => {
        axios.get('/get/conversationList')
        .then((res) => {
            setChats(res.data)
        })
    }

    useEffect(() => {
       getChats()
    }, [])

    const addChat = (e) => {
        e.preventDefault()

        const chatName = prompt('Please enter a chat name')
        const firstMsg = prompt('Please send a welcome message')

        if (chatName && firstMsg) {
          let chatId = ''

          axios.post('/new/conversation', {
            chatName: chatName
          }).then((res) => {
            chatId = res.data._id
          }).then(() => {
            axios.post(`/new/message?id=${chatId}`, {
              message: firstMsg,
              timestamp: Date.now(),
              user: user
            })
          })
        }
      }

    return (
        <div className='sidebar'>

            <div className='sidebar__header'>

                <Avatar onClick={() => auth.signOut()} 
                src={user.photo} className='sidebar__avatar'/>

                <div className='sidebar__input'>
                    <SearchIcon/>
                    <input placeholder='search'/>
                </div>

                <IconButton variant='outlined' className='sidebar__inputButton'>
                    <MessageIcon onClick={addChat}/>
                </IconButton>


            </div>

            <div className='sidebar__chats'>
                {chats.map(({ id, name, timestamp }) => (

                    <SidebarChat key={id} id={id} chatName={name} timestamp={timestamp}/>

                ))}

            </div>

        </div>
    )
}

export default Sidebar
