import './Chat.css';
import MicIcon from '@material-ui/icons/Mic';
import { IconButton } from '@material-ui/core';
import Message from './Message'

function Chat() {

    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([]);

    const sendMessage = (e) => {

        e.preventDefault();
        setInput('');

